package solutions

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func Test_hasAllCodes(t *testing.T) {
	type args struct {
		s string
		k int
	}
	tests := []struct {
		name string
		args args
		want bool
	}{
		{
			name: "Test 1",
			args: args{
				s: "00110110",
				k: 2,
			},
			want: true,
		},
		{
			name: "Test 2",
			args: args{
				s: "0110",
				k: 1,
			},
			want: true,
		},
		{
			name: "Test 3",
			args: args{
				s: "0110",
				k: 2,
			},
			want: false,
		},
		{
			name: "Test 4",
			args: args{
				s: "00110",
				k: 2,
			},
			want: true,
		},
		{
			name: "Test 5",
			args: args{
				s: "",
				k: 20,
			},
			want: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			assert.Equal(t, tt.want, hasAllCodes(tt.args.s, tt.args.k))
		})
	}
}
